const Layout = (props) => {
    return (
        <React.Fragment>
            {props.children}
            <style global jsx>
                {
                    `
                    @import url('https://fonts.googleapis.com/css2?family=Nunito+Sans:wght@200;400;900&display=swap');

                 * {
                     box-sizing: border-box;
                     margin: 0;
                     padding: 0;

                 }

                 html, body {
                     width: 100%;
                     height: 100%;
                 }

                 :root {
                     
                 }
                
                   body {
                    font-family: 'Nunito Sans', sans-serif;
                   }
                 
                
                 #__next {
                     width: 100%;
                     height: 100%;
                 }
                    
                    `
                }
            </style>
        </React.Fragment>
    )
}

export default Layout;
