import  gsap from "gsap";
import {Component} from 'react';

class Gsap extends Component {
    constructor(props){
        super(props);
        // reference to the DOM node
        this.myDiv = null;
        // reference to the animation
        this.greensock = null;
      }

      width = 50

      state = {

          isTo: false
          
      }

      handleClick = async () => {
          this.width += 10;
          await this.setState((prevState) => {
              return {
                isTo : !prevState.isTo
              }
               
          })
          if(this.state.isTo === true) {
            this.greensock = gsap.to(this.myDiv,{width: `${this.width}`,  duration: 2});
          }else {
            this.greensock = gsap.to(this.myDiv,{x: -100, duration: 2});
          }
        
      }

    render(){

   
    
    return(
        <div >
    <div ref={div => this.myDiv = div}>
        <style jsx>
            {
                `
                div {
                    width: 50px;
                    height: 50px;
                    background: red;
                    margin: 100px;
                    transform-origin: 50% 50%;
                    position: absolute;
                }
                
                `
            }
        </style>
    </div>
    <button onClick = {this.handleClick}>
            click
    </button>
    </div>);
    }

}

export default Gsap;
