const LoginBox = () => {
    return(
        <div className="container">
                <div className="heading-wrapper">
                    <div>
                        <h2>Login</h2>
                    </div>
                    <div>
                        <h2>signup</h2>
                    </div>
                   
                </div>
               
                 <form>
                     <input type="text" placeholder="Enter email address"/>
                     <input type="text" placeholder="Username"/>
                     
                     <input type="password" placeholder="password" />
                 </form>
                <button>
                    login
                </button>

            <style jsx>
                {
                    `
                    .container {
                        background: var(--neomorphic-white);
                        width: 350px;
                        height: 550px;
                        box-shadow: 0px 0px 25px 5px rgba(0,0,0,0.2);
                        padding: 30px 10px;
                        border-radius: 10px;
                        display: flex;
                        flex-direction: column;
                        align-items: center;
                        box-shadow: var(--neomorphic-box-shadow);
                        position: relative;
                        
                    }

                    .heading-wrapper {
                        width: 70%;
                        border-radius: 50px;
                        background: red;
                        display: flex;
                        justify-content: center;
                        align-items : center;
                        overflow: hidden;
                        height: 45px;
                        margin-bottom: 20%;
                        box-shadow: var(--neomorphic-box-shadow);
                        background: var(--neomorphic-white);
                    }

                    .heading-wrapper div {
                        width: 50%;
                        box-shadow: var(--neomorphic-box-shadow);
                        background: var(--neomorphic-white);
                        display: flex;
                        justify-content: center;
                        align-items : center;
                        height: 100%;
                    }

                    

                    .container .heading-wrapper div h2 {
                        font-weight: 900;
                        text-transform: uppercase;
                        font-size: 1rem;
                        text-align: center;
                        
                        
                    }

                    .container button {
                        width: 70%;
                        height: 40px;
                        text-transform: uppercase;
                        border: none;
                        outline: none;
                        cursor: pointer;
                        font-size: 0.7rem;
                        font-weight: 900;
                        box-shadow: var(--neomorphic-box-shadow);
                        background: var(--neomorphic-white);
                        color: black;
                        border-radius: 50px;
                        transition: all 0.3s linear;
                        position: absolute;
                        bottom: 15%;
                        left: 50%;
                        transform: translateX(-50%);
                    }
                    
                    .container button:hover {
                        box-shadow: var(--hover-neomorphic-box-shadow);
                    }

                    form {
                        width: 100%;
                        display: flex;
                        align-items : center;
                        flex-direction: column;
                    }

                    form input {
                        width: 80%;
                        min-width: 200px;
                        height: 40px;
                        border: 0;
                        outline: 0;
                        border-radius: 50px;
                        font-size: 1.1rem;
                        padding: 0px 10px;
                        background-color: var(--neomorphic-white);
                        box-shadow:  var(--input-neomorphic-box-shadow);
                        margin: 17px 0;
                         
                    }

                    form input:focus {
                        box-shadow:  var(--input-focus-neomorphic-box-shadow);
                        text-shadow: 1px 1px 0 #FFF;
                    }

                    form input::placeholder {
                        font-size: 0.8rem;
                    }

                    @media only screen and (max-width: 500px) {
                       
                        .container { 
                            width: 96%;
                        }
                      }
                    
                    `
                }
            </style>
        </div>
    )
}

export default LoginBox;
