import Layout from './../components/Layout';
import LoginBox from './../components/LoginBox';
import {Component} from 'react';


class Login extends Component {

    constructor() {
        super();
        this.state = {
            loginButton: {
                isSelected: false
            },
            signupButton: {
                isSelected: true
            }

        }
    }

    render() {
    return(
        <Layout>
            <div className="container">
                <LoginBox loginButton = {this.state.loginButton} signupButton/>
                <style jsx>
                    {`

                        .container {
                            width: 100%;
                            height: 100%;
                            display: flex;
                            justify-content: center;
                            align-items: center;
                            background: var(--neomorphic-white);
                        }

                      
                    
                    `}
                </style>
            </div>
            
        </Layout>
       
    );
                    }
}

export default Login;
