const LoginBox = () => {
    return(
        <div className="container">

                <h2>Login</h2>
                 <form>
                     <input placeholder="Enter email address"/>
                 </form>
                <button>
                    login
                </button>

            <style jsx>
                {
                    `
                    .container {
                        background: var(--neomorphic-white);
                        width: 350px;
                        height: 450px;
                        box-shadow: 0px 0px 25px 5px rgba(0,0,0,0.2);
                        padding: 30px 10px;
                        border-radius: 10px;
                        display: flex;
                        flex-direction: column;
                        align-items: center;
                        box-shadow: var(--neomorphic-box-shadow);
                        position: relative;
                        
                    }

                    .container h2 {
                        font-weight: 900;
                        text-transform: uppercase;
                        font-size: 1rem;
                        text-align: center;
                        margin-bottom: 30px;
                    }

                    .container button {
                        width: 70%;
                        height: 40px;
                        text-transform: uppercase;
                        border: none;
                        outline: none;
                        cursor: pointer;
                        font-size: 0.7rem;
                        font-weight: 900;
                        box-shadow: var(--neomorphic-box-shadow);
                        background: var(--neomorphic-white);
                        color: black;
                        border-radius: 50px;
                        transition: all 0.3s linear;
                        position: absolute;
                        bottom: 25px;
                        left: 50%;
                        transform: translateX(-50%);
                    }
                    
                    .container button:hover {
                        box-shadow: var(--hover-neomorphic-box-shadow);
                    }

                    form {
                        width: 100%;
                        display: flex;
                        justify-content: center;
                    }

                    form input {
                        width: 80%;
                        min-width: 200px;
                        height: 40px;
                        border: 0;
                        outline: 0;
                        border-radius: 50px;
                        font-size: 1.1rem;
                        padding: 0px 10px;
                        background-color: var(--neomorphic-white);
                         box-shadow:  inset 2px 2px 5px #BABECC, 
                        inset -5px -5px 10px var(--neomorphic-white);
                        text-shadow: 1px 1px 0 #FFF;
                    }

                    form input:focus {
                        
                    }

                    form input::placeholder {
                        font-size: 0.8rem;
                    }
                    
                    `
                }
            </style>
        </div>
    )
}

export default LoginBox;
