const ChatBox = () => {
    return (
        <div className = "container">
           
            <div className="chatHeader">
                
                <img src="logo.svg" class="logo" alt="logo of the site" />
                    
                <h2 className="title">
                    WhatsApp
                </h2>
                
            </div>
             
            <style jsx>{`
            
            .container {
                width: 40%;
                min-width: 300px;
                height: 100%;
                border-left: 1px solid rgba(0,0,0,0.1);
                border-right: 1px solid rgba(0,0,0,0.1);
                background-image: url("/default-wallpaper.jpg");
            }

            .chatHeader {
                width: 100%;
                height: 58px;
                background: #25D366;
                display: flex;
                align-items: center;
                padding: 0px 15px;
            }

            .title {
                color: white;
            }

            .logo {
                width: 20px;
                height: 20px;
                margin: 7px;
            }



            `}

            </style>
        </div>
    );
}

export default ChatBox;
