import Layout from './../components/Layout';
import LoginBox from './../components/LoginBox';
import {Component} from 'react';
import Router from 'next/router'


class Login extends Component {

    componentDidMount(){
        const {pathname} = Router
        if(pathname == '/login' ){
           Router.push('/')
        }
    }

    constructor() {
        super();
        this.state = {
            loginButton: {
                isSelected: false
            },
            signupButton: {
                isSelected: true
            }

        }
    }

    handleLoginSignupButtonClick = () => {
        if(this.state.loginButton.isSelected === true) {
            this.setState({
                loginButton: {
                    isSelected: false
                }
            });
        }else {
            this.setState({
                loginButton: {
                    isSelected: true
                }
            });
        }

        if(this.state.signupButton.isSelected === true) {
            this.setState({
                signupButton: {
                    isSelected: false
                }
            });
        }else {
            this.setState({
                signupButton: {
                    isSelected: true
                }
            });
        }
    }



    render() {
    return(
        <Layout>
            <div className="container">
                <LoginBox 
                loginButton = {this.state.loginButton} 
                signupButton = {this.state.signupButton}
                handleLoginSignupButtonClick = {this.handleLoginSignupButtonClick} />
                <style jsx>
                    {`

                        .container {
                            width: 100%;
                            height: 100%;
                            display: flex;
                            justify-content: center;
                            align-items: center;
                            background: var(--neomorphic-white);
                        }

                      
                    
                    `}
                </style>
            </div>
            
        </Layout>
       
    );
                    }
}

export default Login;
