import Layout from './../components/Layout';
import LoginBox from './../components/LoginBox';
import {Component} from 'react';


class Login extends Component {

    constructor() {
        super();
        this.state = {
            loginButton: {
                isSelected: false
            },
            signupButton: {
                isSelected: true
            }

        }
    }

    handleLoginSignupButtonClick = (value) => {
        if(value === 'login') {
            if(this.state.loginButton.isSelected === true) {
                return;
            }else {
                this.setState({
                    loginButton: {
                        isSelected: true
                    },
                    signupButton: {
                        isSelected: false
                    }

                });
            }
        }
        if(value === 'signup') {
            if(this.state.loginButton.isSelected === true) {
                return;
            }else {
                this.setState({
                    loginButton: {
                        isSelected: true
                    },
                    signupButton: {
                        isSelected: false
                    }

                });
            }
        }
       

       
    }



    render() {
    return(
        <Layout>
            <div className="container">
                <LoginBox 
                loginButton = {this.state.loginButton} 
                signupButton = {this.state.signupButton}
                handleLoginSignupButtonClick = {this.handleLoginSignupButtonClick} />
                <style jsx>
                    {`

                        .container {
                            width: 100%;
                            height: 100%;
                            display: flex;
                            justify-content: center;
                            align-items: center;
                            background: var(--neomorphic-white);
                        }

                      
                    
                    `}
                </style>
            </div>
            
        </Layout>
       
    );
                    }
}

export default Login;
